import React from 'react';

const Hero = () => {
  const handleSmoothScroll = (e, targetId) => {
    e.preventDefault();
    const element = document.getElementById(targetId);
    if (element) {
      element.scrollIntoView({
        behavior: 'smooth',
        block: 'start'
      });
    }
  };

  return (
    <section className="hero" id="home">
      <div className="hero-content">
        <h1>Regulating Water Use, Empowering Enterprise</h1>
        <p className="hero-subtitle">
          Balancing Opportunity with Sustainabilityâ€”Join the movement to protect our water and grow your business.
        </p>
        <div className="cta-buttons">
          <a href="signup.html" className="btn btn-secondary">
            <i className="fas fa-edit"></i> Register Your Car Wash
          </a>
          <a 
            href="#checklist" 
            className="btn btn-secondary"
            onClick={(e) => handleSmoothScroll(e, 'checklist')}
          >
            <i className="fas fa-download"></i> Download Compliance Checklist
          </a>
        </div>
      </div>
    </section>
  );
};

export default Hero;
