import React from 'react';
import { Link } from 'react-router-dom';

const About = () => {
  const values = [
    {
      icon: 'fas fa-leaf',
      title: 'Environmental Sustainability',
      description: 'Promoting water conservation and responsible usage through innovative monitoring and recycling technologies.'
    },
    {
      icon: 'fas fa-handshake',
      title: 'Community Empowerment',
      description: 'Supporting informal car wash operators in transitioning to formal, regulated micro-enterprises with training and resources.'
    },
    {
      icon: 'fas fa-chart-line',
      title: 'Economic Growth',
      description: 'Creating opportunities for sustainable business development while ensuring compliance with water regulations.'
    }
  ];

  const aboutStats = [
    { number: '3+', label: 'Years of Service' },
    { number: '85%', label: 'Water Savings Achieved' },
    { number: '500+', label: 'Businesses Supported' },
    { number: '12', label: 'Townships Covered' }
  ];

  return (
    <section className="about-us section-reveal" id="about">
      <div className="container">
        <div className="section-header">
          <h2 className="section-title">About Aqua Balance Tshwane</h2>
          <p className="section-subtitle">Transforming water management through innovation, education, and community partnership</p>
        </div>
        
        <div className="about-content">
          <div className="about-text">
            <div className="about-intro">
              <h3>Our Mission</h3>
              <p>Aqua Balance Tshwane is a pioneering initiative by the City of Tshwane, designed to regulate water usage in the car wash industry while empowering local entrepreneurs to build sustainable businesses. We bridge the gap between environmental stewardship and economic opportunity.</p>
            </div>
            
            <div className="about-values">
              {values.map((value, index) => (
                <div key={index} className="value-item">
                  <i className={value.icon}></i>
                  <div>
                    <h4>{value.title}</h4>
                    <p>{value.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
          
          <div className="about-stats">
            {aboutStats.map((stat, index) => (
              <div key={index} className="about-stat">
                <span className="stat-number">{stat.number}</span>
                <span className="stat-label">{stat.label}</span>
              </div>
            ))}
          </div>
        </div>
        
        <div className="about-cta">
          <h3>Ready to Join Our Community?</h3>
          <p>Become part of the Aqua Balance network and transform your car wash into a sustainable, compliant business.</p>
          <Link to="/signup" className="btn btn-primary">
            <i className="fas fa-user-plus"></i> Register Your Business
          </Link>
        </div>
      </div>
    </section>
  );
};

export default About;
