import React from 'react';
import { Link } from 'react-router-dom';

const About = () => {
  const keyFeatures = [
    {
      icon: 'fas fa-tint',
      title: 'Water Management',
      description: 'Smart monitoring and conservation solutions for car wash businesses.'
    },
    {
      icon: 'fas fa-users',
      title: 'Community Support',
      description: 'Training and resources to help informal operators formalize their businesses.'
    },
    {
      icon: 'fas fa-check-circle',
      title: 'Compliance Made Easy',
      description: 'Simplified regulatory processes and ongoing compliance support.'
    }
  ];

  return (
    <section className="about-us section-reveal" id="about">
      <div className="container">
        <div className="section-header">
          <h2 className="section-title">About Us</h2>
          <p className="section-subtitle">Empowering sustainable car wash businesses in Tshwane</p>
        </div>
        
        <div className="about-content">
          <div className="about-intro">
            <h3>Aqua Balance Tshwane</h3>
            <p>We're a City of Tshwane initiative that helps car wash operators transition to sustainable, water-efficient businesses. Our platform provides regulatory support, training, and tools to ensure compliance while promoting economic growth in local communities.</p>
            
            <div className="features-grid-compact">
              {keyFeatures.map((feature, index) => (
                <div key={index} className="feature-item-compact">
                  <i className={feature.icon}></i>
                  <div>
                    <h4>{feature.title}</h4>
                    <p>{feature.description}</p>
                  </div>
                </div>
              ))}
            </div>
            
            <div className="about-cta-compact">
              <Link to="/signup" className="btn btn-primary">
                <i className="fas fa-user-plus"></i> Get Started
              </Link>
              <Link to="/login" className="btn btn-secondary">
                <i className="fas fa-sign-in-alt"></i> Login
              </Link>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
